import pathlib

import aoc202206 as aoc
import pytest
from aocd.models import Puzzle

PUZZLE_DIR = pathlib.Path(__file__).parent / "example-data"


@pytest.fixture
def puzzleinput():
    puzzle_input = Puzzle(year=2022, day=6).input_data
    return aoc.parse(puzzle_input)


@pytest.fixture
def example1():
    puzzle_input = (PUZZLE_DIR / "06_example1.txt").read_text().strip()
    return aoc.parse(puzzle_input)


