def test_parse_puzzleinput(puzzleinput):
    """Test that input is parsed properly"""
    assert puzzleinput == ''

def test_parse_example1(example1):
    """Test that input is parsed properly"""
    assert example1 == 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'


